import React, { Component } from 'react'

class Input extends Component {
  constructor(props) {
    super(props)

    this.inputRef = React.createRef()
  }

  inputFocus(){
    this.inputRef.current.focus()
  }
  render() {
    return (
      <div>
        <input type="text" ref={this.state.inputRef}></input>
      </div>
    )
  }
}

export default Input
