import React, { Component } from 'react'

class User extends Component {
    constructor(props) {
        super(props)

        this.state = {
           count: 0
        }
      }
      clickCounter = () => {
        this.setState(prevState => {
            return {count: prevState.count + 1}
        })
      }

  render() {
    return (
      <div>
        {this.props.render(this.count, this.clickCounter)}
      </div>
    )
  }
}

export default User
